      if (!this.expressionsVisible()) {
        return this.requireExpressionsVisible();
      }

      if (this.resourcesOpen()) {
        return this.requireResourcesNotOpen();
      }

      if (this.editListMode()) {
        return this.requireNotEditListMode();
      }

